var dataTypes = require('../data_types');

function Account(client) {
  this.client = client;
}

Account.prototype.retrieve = function retrieve(params, callback) {
  if (typeof params === 'function') {
    callback = params;
    params = {};
  }
  var req = dataTypes.EmptyRequest.create(params);
  if (req instanceof Error) {
    return callback(req, null);
  }
  this.client.request('get', 'account', req, function(err, data) {
    if (err) {
      callback(err, null);
    } else {
      var res = new dataTypes.AccountResponse(data);
      if (res instanceof Error) {
        callback(res, null);
      } else {
        callback(null, res);
      }
    }
  });
};

Account.prototype.deleteData = function deleteData(params, callback) {
  if (typeof params === 'function') {
    callback = params;
    params = {};
  }
  var req = dataTypes.EmptyRequest.create(params);
  if (req instanceof Error) {
    return callback(req, null);
  }
  this.client.request('delete', 'account' + '/' + 'data', req, function(err, data) {
    if (err) {
      callback(err, null);
    } else {
      var res = new dataTypes.DeletedResponse(data);
      if (res instanceof Error) {
        callback(res, null);
      } else {
        callback(null, res);
      }
    }
  });
};

module.exports = Account;
